let unescapeData =	function(data) {
    var decodedLength = 0;
    var decodedData = Buffer.alloc(data.length);
    var i=0;
    while (i<data.length) {
    	if (data[i] === 0x01 && i>0) {
    		return unescapeData(data.slice(i));
    	}
    	else if (data[i] === 0x02) {
    		if ((i+1)<data.length) {
    			decodedData[decodedLength++] = data[++i] ^ 0x10;
    		}
    		else {
    			throw new Error(" FRAME_ESCAPE(0x02) byte found at the end of the data.");
    		}
    	}
    	else {
    		decodedData[decodedLength++] = data[i];
    	}
    	i++;
    }
    return decodedData.slice(0, decodedLength);
}

