var bufLength = 7 + msg.payload.data.length;
var buf = Buffer.alloc(bufLength);

var checksum = 0x00;
checksum ^= msg.payload.msgType >> 8;
checksum ^= msg.payload.msgType % 256;
checksum ^= msg.payload.data.length >> 8;
checksum ^= msg.payload.data.length % 256;

buf.writeUInt8(0x01, 0);
buf.writeUInt16BE(msg.payload.msgType, 1);
buf.writeUInt16BE(msg.payload.data.length, 3);

var cpt = 6;
msg.payload.data.map(data => {
  checksum ^= data;
  buf.writeUInt8(data, cpt);    
  cpt++;
});
buf.writeUInt8(checksum, 5);

buf.writeUInt8(0x03, bufLength - 1);
msg.payload = buf;


